        [Test]
        public void TestForExtractNormalTextFromHtml()
        {
            const string html = "<div>This is test." +
                                    "<br>" +
                                    "<p>Just text in paragraph.</p>" +
                                    "<a href='onlyoffice.com'>[click me]</a>" +
                                    "<span>Just text in span.</span>" +
                                    "<b>Just text in bolt.</b>" +
                                    "<img src='logo.png'>" +
                                    "<center>Just text in center.</center>" +
                                    "<div>Just text in div.</div>" +
                                  "</div>";
            var res = MailUtil.ExtractTextFromHtml(html);
            Assert.AreEqual("This is test.\r\n\r\n\r\nJust text in paragraph.\r\n\r\n[click me] (onlyoffice.com)\r\nJust text in span.\r\nJust text in bolt.\r\nJust text in center.\r\nJust text in div.\r\n", res);
        }

