smtp.Connect("mail.myhost.com",8504)
Try
	smtp.Ehlo()
Catch
	smtp.Helo()
End Try
If smtp.Verify("jdoe@myhost.com") Then
	Dim isValid As Boolean = True
	'Address is valid and resides on this server.
smtp.Disconnect()

