message.To.Add("mjohns@otherhost.com","Mike Johns");
message.Subject = "hey!";
message.Attachments.Add("C:\\myfile.doc");
message.BodyHtml.Text = "As promised, the requested document.<br /><br />Regards,<br>John.";
