		server.Port = 43;
		server.Domain = ".com";

		WhoIs whoIs = new WhoIs();
		string result = whoIs.Query(server,"activeup.com");
		Console.WriteLine(result);
	}

 catch(WhoisException we)
	{
		Console.WriteLine("WhoisException : " + we.Message);
	}

	catch(Exception ex)
